console.log("equality test with string: ", "Apple" != "Apple");
console.log("inequality test with string: ", "Apple" === "Apple");
console.log("lowercase function test: ", "HELLOW".toLowerCase() != "hellow");
console.log("equality test with numbers: ", 23 === 23);
console.log("inequality test with numbers: ", 23 != 23);
console.log("greater than test: ", 12 > 10);
console.log("less than test: ", 16 < 14);
console.log("greater than or equals to test: ", 13 <= 13);
console.log("less than or equals to test: ", 13 >= 15);
console.log("and operator test: ", 20 > 10 && 20 == 20);
console.log("or operator test: ", 20 < 10 || 20 != 20);
var fruits = ["apple", "mango", "orange", "banana"];
console.log('test "apple" is in array', fruits.includes("apple"));
console.log('test "grappes" is not in array', fruits.includes("grappes"));
